import { useState, useEffect } from "react";

function App() {
  const [loading, setLoading] = useState(true);
  const [coins, setCoins] = useState([]);
  const [selectOpt, setSelectOpt] = useState("name");
  const [value, setValue] = useState("");
  const [search, setSearch] = useState("");
  const [result, setResult] = useState([]);
  const [nowResult, setNowResult] = useState(false);
  const [afterResult, setAfterResult] = useState(false);

  useEffect(
    () =>
      fetch("https://api.coinpaprika.com/v1/tickers")
        .then((response) => response.json())
        .then((json) => {
          setCoins(json);
          setLoading(false);
        }),
    []
  );

  const onChangeOpt = (event) => setSelectOpt(event.target.value);
  const onChangeIn = (event) => setValue(event.target.value);
  const onSubmit = (event) => {
    setSearch(value);
    event.preventDefalut();
    let isResult = false;
    if (selectOpt === "name") {
      coins.map((coin) => {
        if (coin.name.toUpperCase() === search.toUpperCase()) {
          isResult = true;
          setNowResult(true);
          setResult(coin);
          setAfterResult(true);
          return;
        }
      });
    } else if (selectOpt === "symbol") {
      coins.map((coin) => {
        if (coin.symbol.toUpperCase() === search.toUpperCase()) {
          isResult = true;
          setNowResult(true);
          setResult(coin);
          setAfterResult(true);
          return;
        }
      });
    }
    if (!isResult) {
      setAfterReslt(false);
      setNowResult(false);
    }
    setValue("");
  };
  return (
    <div>
      <h1>The Coins! {loading ? "" : `(${coins.length})`}</h1>
      <form onSubmit={onSubmit}>
        <select onChange={onChangeOpt}>
          <option value="name">Name</option>
          <option value="symbol">Symbol</option>
        </select>
        <input
          value={value}
          onChange={onChangeIn}
          type="text"
          placeholder={selectOpt}
        />
        <input type="submit" value="Find Coin" />
      </form>
      {nowResult ? (
        <form>
          <h2>You can buy this coin : {result.symbol}</h2>
        </form>
      ) : null}
      {afterResult ? (
        <form>
          <h2>Search Again. There is no coin information.</h2>
        </form>
      ) : null}

      {loading ? (
        <strong>Loading...</strong>
      ) : (
        <ul>
          {coins.map((coin) => (
            <li key={coin.id}>
              {coin.name} ({coin.symbol}) : ${coin.quotes.USD.price}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
