import { useEffect } from "react";
import { useParams } from "react-router-dom";

function Detail() {
  const [loading, setLoding] = useState(false);
  const { id } = useParams();
  const getMovie = async () => {
    const json = await (
      await fetch(
        `https://yts.mx/api/v2/list_movies.json?minimum_rating=9&sort_by=id=${id}`
      )
    ).json();
    console.log(json);
  };
  useEffect(() => {
    getMovie();
  }, []);
  return (
    <div>
      <form>
        {loading ? (
          <strong>Loading...</strong>
        ) : (
          <h1>This {json.data.movies.title}</h1>
        )}
      </form>
    </div>
  );
}

export default Detail;
